    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine("Hello World!");

            var attr = Attribute.Load();

            XmlSerializer formatter = new XmlSerializer(typeof(yml_catalog));

            yml_catalog r = new yml_catalog();

            // получаем поток, куда будем записывать сериализованный объект


            List<Product> lp = new List<Product>();

            StringBuilder sb = new StringBuilder();

            var files = Directory.GetFiles(@"C:\Users\sergey\Downloads\Telegram Desktop\SiteProducts\", "*.xml");
            foreach (var file in files)
            {
                XDocument xd = XDocument.Load(file);
                var products = xd.Root.Elements("Product");
                Product pr;
                var prop = typeof(Product).GetProperties();
                foreach (var prod in products)
                {
                    pr = new Product();
                    foreach (var prodVal in prod.Elements())
                    {
                        var ppV = prop.Where(w => w.Name == prodVal.Name || w.Name == prodVal.Name.LocalName).FirstOrDefault();
                        if (ppV != null)
                        {
                            ppV.SetValue(pr, prodVal.Value);
                        }
                        else if (prodVal.Name.NamespaceName == "attribute")
                        {
                            sb.Append(prodVal.Name.LocalName + " = " + Environment.NewLine);
                            if (!string.IsNullOrWhiteSpace(prodVal.Value) && !prodVal.Name.NamespaceName.EndsWith("_data"))
                            {
                                if (prodVal.Name.LocalName == "pa_brand")
                                {
                                    pr.brand = prodVal.Value;
                                }
                                else
                                {
                                    var n = prodVal.Name.LocalName.Replace("pa_", "");
                                    //var at = attr.Where(wh => wh.attribute_name.Trim() == n).ToList();

                                    try
                                    {
                                        var _at = attr.Where(wh => wh.attribute_name != null).ToList();
                                        var at = _at.Where(ww => ww.attribute_name.Trim() == n).First().attribute_label;
                                        pr.attributes.Add(at.Trim(), prodVal.Value);
                                    }
                                    catch { }
                                }
                            }
                        }
                        else
                        {
                            if (!string.IsNullOrWhiteSpace(prodVal.Value) && !prodVal.Name.NamespaceName.EndsWith("_data"))
                            {
                                Console.WriteLine(prodVal.Name + ": " + prodVal.Value);
                            }

                        }
                    }
                    lp.Add(pr);



                    string _str = sb.ToString();
                }
            }
            int i = 1;
            var _w = lp.Select(s => s.product_cat).Distinct().Select(ss => new categor1y(i++, ss)).ToList();
            r.shop.categories.AddRange(_w.Select(s => new category() { id = s.id.ToString(), Value = s.PathName }));

            foreach (var we in lp)
            {
                var f = new offer()
                {
                    available = "true",
                    categoryId = _w.Where(w => w.PathName == we.product_cat).First().id.ToString(), //.Select(s=>s.id.ToString().First()),
                    description = we.post_content,
                    id = we.ID,
                    name = we.post_title,
                    //picture = we.images,
                    price = we._regular_price,
                    url = we.product_page_url,
                    vendor = we.brand,
                };

                foreach (string www in we.pictures)
                {
                    f.picture.Add(www);
                }
                foreach (var www in we.attributes)
                {
                    f.p.Add(new param()
                    {
                           name = www.Key,
                            Value=www.Value
                    });
                }

                r.shop.offers.Add(f);
            }

            using (FileStream fs = new FileStream("r.xml", FileMode.OpenOrCreate))
            {
                formatter.Serialize(fs, r);

                Console.WriteLine("Объект сериализован");
            }

        }
    }




    [Serializable]
    public class Product
    {
        string _images;

        public string post_title { get; set; }
        public string ID { get; set; }
        public string _regular_price { get; set; }
        public string _sku { get; set; }
        public string product_page_url { get; set; }
        public string post_content { get; set; }
        public string product_cat { get; set; }

        public string brand { get; set; }

        public List<string> pictures { get; set; } = new List<string>();

        public string images
        {
            get { return _images; }
            set
            {
                if (!string.IsNullOrWhiteSpace(value))
                {
                    _images = value.Split("!")[0].Trim();

                    foreach(var i in value.Split("!"))
                    {
                        if(i.Contains("http://") || i.Contains("https://"))
                        {
                            if (i.Trim().StartsWith("http"))
                            {
                                pictures.Add(i.Trim());
                            }
                            else
                            {
                                pictures.Add("https://" + i.Split("https://")[1]);
                            }
                        }
                    }
                }
            }
        }

        public Dictionary<string, string> attributes { get; set; } = new Dictionary<string, string>();
    }

    public class categor1y
    {
        public int id { get; set; }
        public string PathName { get; set; }
        public string Name
        {
            get
            {
                var w = PathName.Split(">");
                if (w.Length > 1)
                {
                    var _w = w[w.Length - 1].Split("|");
                    if (_w.Length > 1)
                    {
                        return _w[_w.Length - 1];
                    }
                    else
                    {
                        return w[w.Length - 1];
                    }
                }
                else
                {
                    return PathName;
                }
            }
        }

        public categor1y(int id, string PathName)
        {
            this.id = id;
            this.PathName = PathName; 
        }
    }

